const anchors=document.querySelectorAll('a[href*="#"]');for(let e of anchors)e.addEventListener("click",(function(t){t.preventDefault();const o=e.getAttribute("href");document.querySelector(""+o).scrollIntoView({behavior:"smooth",block:"start"})}));const menuBtn=document.querySelector(".header__burger"),menu=document.querySelector(".header__menu-list"),body=document.querySelector(".body");menuBtn.addEventListener("click",(()=>{menu.classList.toggle("header__menu-list--active"),menuBtn.classList.toggle("header__burger--active"),body.classList.toggle("body--lock")})),menu.addEventListener("click",(()=>{menu.classList.remove("header__menu-list--active"),menuBtn.classList.remove("header__burger--active"),body.classList.remove("body--lock")}));