const anchors=document.querySelectorAll('a[href*="#"]');for(let e of anchors)e.addEventListener("click",(function(t){t.preventDefault();const r=e.getAttribute("href");document.querySelector(""+r).scrollIntoView({behavior:"smooth",block:"start"})}));let menuBtn=document.querySelector(".header__burger"),menu=document.querySelector(".header__menu");menuBtn.addEventListener("click",(()=>{menu.classList.toggle("header__menu--active")}));